package com.andreytim.jafar.problems.arrstr;

/**
 * Wildcard Matching (LeetCode)
 * https://oj.leetcode.com/problems/wildcard-matching/
 *
 * Created by shpolsky on 23.11.14.
 */
public class P215_WildcardMatching {

    public boolean matches(String str, String pattern) {
        char[] sa = str.toCharArray();
        char[] pa = pattern.toCharArray();
        int si = 0, pi = 0;
        int spi = -1, ssi = 0;
        while (si < sa.length) {
            if (pi < pa.length && (pa[pi] == '?' || pa[pi] == sa[si])) {
                si++; pi++;
            } else if (pi < pa.length && (pa[pi] == '*')) {
                spi = pi++; ssi = si;
            } else if (spi != -1) {
                pi = spi+1; si = ++ssi;
            } else return false;
        }
        while (pi < pa.length && pa[pi] == '*') pi++;
        return pi == pa.length;
    }

    private static void test(String s, String p) {
        System.out.printf("Input: string=%s, pattern=%s; Result: %b\n", s, p,
                new P215_WildcardMatching().matches(s, p));
    }

    public static void main(String[] args) {
        test("aeebbccd","a*bbc?d");
        test("aeebbccd","e*bbc?d");
        test("aa","a");
        test("aa","aa");
        test("aaa","aa");
        test("aa", "*");
        test("aa", "a*");
        test("ab", "?*");
        test("aab", "c*a*b");
        test("","**");
    }

}
